package simple;

import org.pvemu.dofusmapparser.DofusMapParser;
import org.pvemu.dofusmapparser.data.DofusMap;
import org.pvemu.dofusmapparser.factory.DofusMapFactory;

/**
 *
 * @author Vincent Quatrevieux <quatrevieux.vincent@gmail.com>
 */
public class Example1 {
    public static void main(String[] args){
        System.out.println("Example 1");
        
        //load the map factory
        DofusMapFactory factory = DofusMapParser.getCachedDofusMapFactory();
        
        //get the map datas
        int id = 10000;
        int width = 15;
        int height = 17;
        int background = 341;
        int ambianceId = 0;
        int musicId = 128;
        boolean outdoor = true;
        int capabilities = 98;
        String mapData = "HhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhfDeaaa0LHhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhaaeaaaaaGhf_-";
        
        DofusMap map;
        
        try{
            map = factory.buildMap(id, width, height, background, ambianceId, musicId, outdoor, capabilities, mapData); //and build the map !
        }catch(Exception e){ //throws exceptions on bad map data length (width and height don't correspond with MapData length) or invalid base 64 char
            e.printStackTrace();
            return;
        }
        
        //and you can use the map !
        System.out.println(map.getCell(5));
    }
}
