package org.pvemu.dofusmapparser.compressor;

import org.junit.Test;
import static org.junit.Assert.*;
import org.pvemu.dofusmapparser.DofusMapParser;
import org.pvemu.dofusmapparser.data.DofusMap;
import org.pvemu.dofusmapparser.factory.exception.BadMapDataException;
import org.pvemu.dofusmapparser.factory.exception.BadMapSizeException;

/**
 *
 * @author Vincent Quatrevieux <quatrevieux.vincent@gmail.com>
 */
public class DofusMapCompressorImplTest {
    
    public DofusMapCompressorImplTest() {
    }

    /**
     * Test of generateMapData method, of class DofusMapCompressorImpl.
     */
    @Test
    public void testGenerateMapData() throws BadMapDataException, BadMapSizeException {
        System.out.println("generateMapData");
        
        int id = 10000;
        int width = 15;
        int height = 17;
        int background = 341;
        int ambianceId = 0;
        int musicId = 128;
        boolean outdoor = true;
        int capabilities = 98;
        String mapData = "HhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhaaeaaaaaHhfDeaaa0LHhfDeaaaaaHhfDeaaaaaHhfDeaaaaaHhaaeaaaaaGhf_-";
        
        DofusMap map = DofusMapParser.getImmutableDofusMapFactory().buildMap(id, width, height, background, ambianceId, musicId, outdoor, capabilities, mapData);
        
        DofusMapCompressor compressor = new DofusMapCompressorImpl();
        assertEquals(mapData, compressor.generateMapData(map));
    }
    
}
